'use strict';

/**
 * Created by xg on 2017/7/28.
 *
 * 精确值类型数据转换工具
 */
var _ = require('lodash');

var _require = require('yylib-utils/MathUtil'),
    execute = _require.execute;

var NumberFormatUtils = {
    /**
     * 小数转百分比显示(带精度)
     * @param value 值
     * @param precision 精度
     */
    decimalToPercent: function decimalToPercent(value, precision) {
        if (value == null || !_.isNumber(value) || !_.isInteger(precision)) {
            return "";
        }
        var val100 = execute(value, 100.00, 2, precision);
        var valStr = val100 + "";
        var rs = valStr.indexOf('.');
        if (rs < 0) {
            rs = valStr.length;
            valStr += '.';
        }
        while (valStr.length <= rs + precision) {
            valStr += '0';
        }
        return valStr + "%";
    }
};
module.exports = NumberFormatUtils;