var React = require('react');
var YYClass = require('../base/YYClass');
var PropTypes = YYClass.PropTypes;
var field = YYClass.field;

var _require = require('antd'),
    BackTop = _require.BackTop;
//定义META


var uiMeta = {
    name: 'backtop',
    fields: [
    //field参数列表：字段属性名|属性默认值|属性类型|是否必填|属性描述
    field('visibilityHeight', 400, PropTypes.number, false, '滚动高度达到此参数值才出现'), field('onClick', null, PropTypes.func, false, '点击按钮的回调函数'), field('target', undefined, PropTypes.func, false, '返回监听滚动事件的DOM节点，注意：结合YYScrollbar使用时，返回yyui-scrollbar下的第一个DOM节点')]
};
var YYBacktop = YYClass.create({
    uiMeta: uiMeta,
    render: function render() {
        return React.createElement(
            'div',
            { className: this.className() },
            React.createElement(
                BackTop,
                this.props,
                this.props.children
            )
        );
    }
});
module.exports = YYBacktop;