'use strict';

/**
 * 微标数组件
 */
var React = require('react');
var YYClass = require('../base/YYClass');
var PropTypes = YYClass.PropTypes;
var field = YYClass.field;

var _require = require('antd'),
    Badge = _require.Badge;

require('./style/index.less');
//定义META
var uiMeta = {
    name: 'badge',
    fields: [
    //field参数列表：字段属性名|属性默认值|属性类型|是否必填|属性描述
    field('count', null, PropTypes.number, false, '展示的数字,>overflowCount时，显示overflowCount+'), field('overflowCount', 99, PropTypes.number, false, '展示封顶的数字值'), field('dot', false, PropTypes.bool, false, '不展示数字，只有一个小红点')]
};
var YYBadge = YYClass.create({
    uiMeta: uiMeta,
    render: function render() {
        return React.createElement(
            'div',
            { className: this.className() },
            React.createElement(
                Badge,
                this.props,
                this.props.children
            )
        );
    }

});
module.exports = YYBadge;