var React = require('react');
var YYClass = require('../base/YYClass');
var PropTypes = YYClass.PropTypes;
var field = YYClass.field;
var Chart = require('rc-echarts');
var echarts = require('echarts');
var uiMeta = {
    name: 'chart',
    fields: [
    //field参数列表：字段属性名|属性默认值|属性类型|是否必填|属性描述
    field('options', '', PropTypes.object, true, '')]
};

var YYChart = YYClass.create({
    uiMeta: uiMeta,
    statics: {
        Line: Chart.Line,
        Bar: Chart.Bar,
        Pie: Chart.Pie,
        Cloud: Chart.Cloud,
        Map: Chart.Map,
        Series: Chart.Series,
        echarts: echarts
    },
    render: function render() {
        return React.createElement(
            Chart,
            this.props,
            this.props.children
        );
    }
});

module.exports = YYChart;