'use strict';

/**
 * 定义一个应用容器
 */
require('./style/index.less');
var YYClass = require('../base/YYClass');
var YYNotification = require('../notification/YYNotification');
var uiMeta = {
    name: "app"
};
var YYApp = YYClass.create({
    uiMeta: uiMeta,
    componentDidMount: function componentDidMount() {
        //捕获全局错误消息
        window.onerror = function (msg, file, line, col, error) {
            YYNotification.error({ message: '脚本发生错误', description: msg });
        };
        //a.a = false;
    },
    componentWillUnMount: function componentWillUnMount() {
        window.onerror = null;
    }
});

module.exports = YYApp;