'use strict';

/**
 * 用于表格配置项rowselection API 描述
 */
var React = require('react');
var YYClass = require('../base/YYClass');
var PropTypes = YYClass.PropTypes;
var field = YYClass.field;
//定义META
var uiMeta = {
    name: 'rowselection',
    fields: [
    //field参数列表：字段属性名|属性默认值|属性类型|是否必填|属性描述
    field('type', 'checkbox', PropTypes.oneOf(['checkbox', 'readio']), false, '多选/单选，checkbox or radio'), field('selectedRowKeys', [], PropTypes.array, false, '指定选中项的 key 数组，需要和 onChange 进行配合'), field('onChange', null, PropTypes.func, false, '选中项发生变化的时的回调，Function(selectedRowKeys, selectedRows)'), field('getCheckboxProps', null, PropTypes.func, false, '选择框的默认属性配置，Function(record)'), field('onSelect', null, PropTypes.func, false, '用户手动选择/取消选择某列的回调，Function(record, selected, selectedRows)'), field('onSelectAll', null, PropTypes.func, false, '用户手动选择/取消选择所有列的回调')]
};
var rowSelection = YYClass.create({
    uiMeta: uiMeta
});
module.exports = rowSelection;