var React = require('react');
var YYClass = require('../base/YYClass');
var PropTypes = YYClass.PropTypes;
var field = YYClass.field;

var _require = require('antd'),
    Timeline = _require.Timeline;

var TimelineItem = Timeline.Item;
//定义META
var uiMeta = {
    name: 'timelineitem',
    fields: [
    //field参数列表：字段属性名|属性默认值|属性类型|是否必填|属性描述
    field('color', 'blue', PropTypes.string, false, '指定圆圈颜色 blue, red, green，或自定义的色值'), field('dot', '', PropTypes.element, '自定义时间轴点')]
};
var YYTimelineItem = YYClass.create({
    uiMeta: uiMeta,
    render: function render() {
        return React.createElement(
            TimelineItem,
            this.props,
            this.props.children
        );
    }
});
module.exports = YYTimelineItem;